package ga.rugal.gitcleaner.maven.mojo;import lombok.SneakyThrows;import lombok.extern.slf4j.Slf4j;import org.apache.maven.plugin.MojoExecutionException;import org.apache.maven.plugin.MojoFailureException;import org.junit.Test;import org.junit.runner.RunWith;import org.junit.runners.Parameterized;import org.junit.runners.Parameterized.Parameters;@RunWith(Parameterized.class)@Slf4jpublic class FindMojoTest extends AbstractMojoTestBase {  @Parameters  public static String[] data() {    return new String[]{      "git_folder",    };  }  @SneakyThrows  @Test  public void canSuccess() {    LOG.info("[{}] on [{}]", SUCCESS, this.test);    this.getMojo(FindMojo.GOAL, this.test, SUCCESS).execute();  }  @SneakyThrows  @Test(expected = MojoExecutionException.class)  public void canFail() {    LOG.info("[{}] on [{}]", FAIL, this.test);    this.getMojo(FindMojo.GOAL, this.test, FAIL).execute();  }}